function crypt(e,b){var f="";for(var c=0;c<e.length;c++){var a,g,d;a=e[c].charCodeAt(0);g=b[c%b.length].charCodeAt(0);d=String.fromCharCode(a+g);f=f.concat(d)}return f}function decrypt(g,c){var a="";for(var d=0;d<g.length;d++){var b,f,e;b=g[d].charCodeAt(0);f=c[d%c.length].charCodeAt(0);e=String.fromCharCode(b-f);a=a.concat(e)}return a}var message="Hello World !";var pass="MyPassword";if(decrypt(crypt(message,pass),pass)!==message){console.error("Error in Max1Truc's Crypt library !")};